RSpec.describe '/grids', type: :request do
  # This should return the minimal set of attributes required to create a valid
  # Grid. As you add validations to Grid, be sure to
  # adjust the attributes here as well.
  let(:valid_attributes) do
    {
      name: 'Grid',
      title: 'Singleton',
      col: 1
    }
  end

  let(:invalid_attributes) do
    {
      name: 'Grid',
      title: 'Singleton',
      col: -1
    }
  end

  # This should return the minimal set of values that should be in the headers
  # in order to pass any filters (e.g. authentication) defined in
  # GridsController, or in your router and rack
  # middleware. Be sure to keep this updated too.
  let(:valid_headers) do
    {}
  end

  describe 'GET /index' do
    it 'renders a successful response' do
      Grid.create! valid_attributes
      get grids_url, headers: valid_headers, as: :json
      expect(response).to be_successful
    end
  end

  describe 'GET /show' do
    it 'renders a successful response' do
      grid = Grid.create! valid_attributes
      get grid_url(grid), as: :json
      expect(response).to be_successful
    end
  end

  describe 'POST /create' do
    context 'with valid parameters' do
      it 'creates a new Grid' do
        expect do
          post grids_url,
               params: { grid: valid_attributes }, headers: valid_headers, as: :json
        end.to change(Grid, :count).by(1)
      end

      it 'renders a JSON response with the new grid' do
        post grids_url,
             params: { grid: valid_attributes }, headers: valid_headers, as: :json
        expect(response).to have_http_status(:created)
        expect(response.content_type).to match(a_string_including('application/json; charset=utf-8'))
      end
    end

    context 'with invalid parameters' do
      it 'does not create a new Grid' do
        expect do
          post grids_url,
               params: { grid: invalid_attributes }, as: :json
        end.to change(Grid, :count).by(0)
      end

      it 'renders a JSON response with errors for the new grid' do
        post grids_url,
             params: { grid: invalid_attributes }, headers: valid_headers, as: :json
        expect(response).to have_http_status(422)
        expect(response.content_type).to eq('application/json; charset=utf-8')
      end
    end
  end

  describe 'PATCH /update' do
    context 'with valid parameters' do
      let(:new_attributes) do
        {
          name: 'Grid',
          title: 'Singleton',
          col: 1,
          size: '2:1'
        }
      end

      it 'updates the requested grid' do
        grid = Grid.create! valid_attributes
        patch grid_url(grid),
              params: { grid: new_attributes }, headers: valid_headers, as: :json
        grid.reload
        expect(grid.size).to eq('2:1')
      end

      it 'renders a JSON response with the grid' do
        grid = Grid.create! valid_attributes
        patch grid_url(grid),
              params: { grid: new_attributes }, headers: valid_headers, as: :json
        expect(response).to have_http_status(:ok)
        expect(response.content_type).to eq('application/json; charset=utf-8')
      end
    end

    context 'with invalid parameters' do
      it 'renders a JSON response with errors for the grid' do
        grid = Grid.create! valid_attributes
        patch grid_url(grid),
              params: { grid: invalid_attributes }, headers: valid_headers, as: :json
        expect(response).to have_http_status(422)
        expect(response.content_type).to eq('application/json; charset=utf-8')
      end
    end
  end

  describe 'DELETE /destroy' do
    it 'destroys the requested grid' do
      grid = Grid.create! valid_attributes
      expect do
        delete grid_url(grid), headers: valid_headers, as: :json
      end.to change(Grid, :count).by(-1)
    end
  end
end
